INSERT INTO exercises (name, difficulty, tips, link) VALUES ('Band Bayesian Curl', 0, 'Attach band to a low anchor point. Point your palms straight forward. Flex your elbow until you feel your biceps contract. Then fully extend elbows. ', 'https://youtu.be/7f1kaMkOdk4');
INSERT INTO muscle_groups_in_exercises (exerciseID, muscleID) VALUES (17, 1);
