INSERT INTO muscle_groups_in_exercises (exerciseID, muscleID) VALUES (18, 1);
INSERT INTO equipment_in_exercises (exerciseID, equipmentID) VALUES (18, 6);
INSERT INTO exercises (name, difficulty, tips, link) VALUES ('Band Bayesian Reverse Curl', 1, 'Attach band to a low anchor point. . Point your palms toward your thighs. Flex your elbow until you feel your biceps contract. Then fully extend elbows. ', 'https://youtu.be/Vz8hrIwDKVg');
INSERT INTO muscle_groups_in_exercises (exerciseID, muscleID) VALUES (19, 1);
INSERT INTO equipment_in_exercises (exerciseID, equipmentID) VALUES (19, 6);
INSERT INTO exercises (name, difficulty, tips, link) VALUES ('Dumbbell Reverse Curl', 1, 'Grab the dumbbells with a pronated (overhand) grip. You can do this exercise thumbless if it''s more comfortable on your wrists. Flex at the elbows until your biceps touch your forearms. Try not to let your elbows flair outward. ', 'https://youtu.be/mEQLEDenxsI');
INSERT INTO muscle_groups_in_exercises (exerciseID, muscleID) VALUES (20, 1);
INSERT INTO equipment_in_exercises (exerciseID, equipmentID) VALUES (20, 2);
INSERT INTO exercises (name, difficulty, tips, link) VALUES ('Barbell Reverse Curl', 2, 'Take a double overhand grip that''s about shoulder width. Flex your elbows while keeping your elbows tucked in. Try not to let them flare out. Curl until your forearm presses into your bicep. Then fully extend your elbows at the bottom of each rep. ', 'https://youtu.be/Hc0eZZpbJz4');
INSERT INTO muscle_groups_in_exercises (exerciseID, muscleID) VALUES (21, 1);
INSERT INTO equipment_in_exercises (exerciseID, equipmentID) VALUES (21, 1);
INSERT INTO exercises (name, difficulty, tips, link) VALUES ('Dumbbell Incline Hammer Curl', 0, 'Hold the dumbbells with a neutral grip (thumbs facing the ceiling). Seated on an incline bench. Slowly lift the dumbbell up to chest height Return to starting position and repeat. ', '');
INSERT INTO muscle_groups_in_exercises (exerciseID, muscleID) VALUES (22, 1);
INSERT INTO equipment_in_exercises (exerciseID, equipmentID) VALUES (22, 2);
INSERT INTO exercises (name, difficulty, tips, link) VALUES ('Dumbbell Incline Reverse Curl', 0, 'Lay on an incline bench. Grab the dumbbells with a pronated (overhand) grip. You can do this exercise thumbless if it''s more comfortable on your wrists. Flex at the elbows until your biceps touch your forearms. Try not to let your elbows flair outward. ', '');
INSERT INTO muscle_groups_in_exercises (exerciseID, muscleID) VALUES (23, 1);
INSERT INTO equipment_in_exercises (exerciseID, equipmentID) VALUES (23, 2);
INSERT INTO exercises (name, difficulty, tips, link) VALUES ('Dumbbell Incline Zottman Curl', 1, 'Lay on an incline bench with your palms facing forward. Flex at the elbows until your forearms touch your biceps. When you hit the top of the movement, rotate your palms and then lower the dumbbells slowly. ', '');
INSERT INTO muscle_groups_in_exercises (exerciseID, muscleID) VALUES (24, 1);
INSERT INTO equipment_in_exercises (exerciseID, equipmentID) VALUES (24, 2);
INSERT INTO exercises (name, difficulty, tips, link) VALUES ('Dumbbell Single Arm Preacher Curl', 0, 'Start with a with a dumbbell in one hand and your elbow resting on the bench. From this starting position, curl the dumbbell up towards your shoulder, keeping your upper arm stationary on the bench. ', '');
INSERT INTO muscle_groups_in_exercises (exerciseID, muscleID) VALUES (25, 1);
INSERT INTO equipment_in_exercises (exerciseID, equipmentID) VALUES (25, 2);
INSERT INTO exercises (name, difficulty, tips, link) VALUES ('Dumbbell Single Arm Spider Curl', 0, 'Lay on an incline or preacher curl bench. Let your arms hang freely off the edge of the bench. Fully flex your elbow and fully extend your elbow on each rep. ', '');
INSERT INTO muscle_groups_in_exercises (exerciseID, muscleID) VALUES (26, 1);
INSERT INTO equipment_in_exercises (exerciseID, equipmentID) VALUES (26, 2);
INSERT INTO exercises (name, difficulty, tips, link) VALUES ('Dumbbell Spider Curl', 0, 'Start by placing your forearms on a flat bench and holding a dumbbell in each hand. From this starting position, curl the dumbbells up towards your shoulders, keeping your elbows close to your body and your core engaged. Slowly lower the dumbbells back down to the starting position, and repeat for the desired number of repetitions. ', '');
INSERT INTO muscle_groups_in_exercises (exerciseID, muscleID) VALUES (27, 1);
INSERT INTO equipment_in_exercises (exerciseID, equipmentID) VALUES (27, 2);
INSERT INTO exercises (name, difficulty, tips, link) VALUES ('Dumbbell Twisting Curl', 0, 'Start by standing with your feet shoulder-width apart and holding a dumbbell in each hand. Bring the dumbbells to your sides, with your palms facing inward and your elbows bent. From this starting position, curl the dumbbells up towards your shoulders while rotating your wrists so that your palms face your shoulders at the top of the movement. ', '');
INSERT INTO muscle_groups_in_exercises (exerciseID, muscleID) VALUES (28, 1);
INSERT INTO equipment_in_exercises (exerciseID, equipmentID) VALUES (28, 2);
INSERT INTO exercises (name, difficulty, tips, link) VALUES ('Plate Bicep Curl', 0, '', '');
INSERT INTO muscle_groups_in_exercises (exerciseID, muscleID) VALUES (29, 1);
INSERT INTO equipment (name) VALUES ('Plate');
INSERT INTO equipment_in_exercises (exerciseID, equipmentID) VALUES (29, 7);
INSERT INTO exercises (name, difficulty, tips, link) VALUES ('Plate Curl To Overhead Press', 1, 'Stand with feet shoulder-width apart and the plate in front of the torso, palms facing forward. Curl the plate up to the front of the shoulder while keeping the elbows close to the body. Rotate the palms to face forward and press the plate overhead, locking out the arms. Lower the plate back to the starting position, keeping control of it, and repeat for desired reps. ', '');
INSERT INTO muscle_groups_in_exercises (exerciseID, muscleID) VALUES (30, 1);
INSERT INTO equipment_in_exercises (exerciseID, equipmentID) VALUES (30, 7);
INSERT INTO exercises (name, difficulty, tips, link) VALUES ('Plate Curl To Overhead Press To Tricep Extension', 2, 'Stand with feet shoulder-width apart and the plate in front of the torso, palms facing forward. Curl the plate up to the front of the shoulder while keeping the elbows close to the body. Rotate the palms to face forward and press the plate overhead, locking out the arms. Lower the plate back behind the head to the starting position of a tricep extension. Push the plate back up overhead, then lower it back to the starting position and repeat for desired reps. ', '');
INSERT INTO muscle_groups_in_exercises (exerciseID, muscleID) VALUES (31, 1);
INSERT INTO equipment_in_exercises (exerciseID, equipmentID) VALUES (31, 7);
INSERT INTO exercises (name, difficulty, tips, link) VALUES ('Plate Squat Hold Curl', 1, 'Lower into a squat position, keeping the plate at chest height. Slowly curl the plate towards your shoulders, keeping your elbows close to your body. Hold for a moment at the top of the curl before lowering the plate back to chest height. Repeat the curls as you hold the squat position. ', '');
INSERT INTO muscle_groups_in_exercises (exerciseID, muscleID) VALUES (32, 1);
INSERT INTO equipment_in_exercises (exerciseID, equipmentID) VALUES (32, 7);
INSERT INTO exercises (name, difficulty, tips, link) VALUES ('Band High Curl', 0, 'Anchor a band to a high point. Start with your feet staggered and your elbows high. Flex your elbows while keeping your arms high. Curl the band to your forehead. ', '');
INSERT INTO muscle_groups_in_exercises (exerciseID, muscleID) VALUES (33, 1);
INSERT INTO equipment_in_exercises (exerciseID, equipmentID) VALUES (33, 6);
INSERT INTO exercises (name, difficulty, tips, link) VALUES ('Band High Hammer Curl', 0, 'Start with the band anchored to something high with your feet staggered and a neutral grip. Keep your elbows high. Curl until your fists are at your forehead. ', '');
INSERT INTO muscle_groups_in_exercises (exerciseID, muscleID) VALUES (34, 1);
INSERT INTO equipment_in_exercises (exerciseID, equipmentID) VALUES (34, 6);
INSERT INTO exercises (name, difficulty, tips, link) VALUES ('Band High Reverse Curl', 1, 'Start with a band attached to a high point, your feet staggered, and an overhand grip. Flex your elbows pulling your forearms to your biceps. Your fists should land by your forehead. ', '');
INSERT INTO muscle_groups_in_exercises (exerciseID, muscleID) VALUES (35, 1);
INSERT INTO equipment_in_exercises (exerciseID, equipmentID) VALUES (35, 6);
INSERT INTO exercises (name, difficulty, tips, link) VALUES ('Band Curl', 0, 'Stand with your feet shoulder-width apart, with the band under both feet. Grasp the handles of the band with both hands, keeping your arms straight and extended in front of you. Keep your back straight, abs engaged, and shoulders relaxed as you curl your hands towards your shoulders, bending at the elbows. ', '');
INSERT INTO muscle_groups_in_exercises (exerciseID, muscleID) VALUES (36, 1);
INSERT INTO equipment_in_exercises (exerciseID, equipmentID) VALUES (36, 6);
INSERT INTO exercises (name, difficulty, tips, link) VALUES ('Band Reverse Curl', 1, 'Start by standing on the center of the band with both feet, shoulder-width apart. Hold the band with both hands, palms facing down, arms extended straight in front of you. Slowly curl your hands towards your shoulders, pulling the band tight, then lower your arms back to the starting position, keeping tension on the band throughout the exercise. ', '');
INSERT INTO muscle_groups_in_exercises (exerciseID, muscleID) VALUES (37, 1);
INSERT INTO equipment_in_exercises (exerciseID, equipmentID) VALUES (37, 6);
INSERT INTO exercises (name, difficulty, tips, link) VALUES ('Dumbbell Concentration Curl', 0, 'Sit on a flat bench with your feet flat on the floor and your back straight. Lean forward and place your elbow on the inside of your leg. Curl the dumbbell while keeping your elbow against your leg. Make sure to fully extend your elbow on each rep. ', '');
INSERT INTO muscle_groups_in_exercises (exerciseID, muscleID) VALUES (38, 1);
INSERT INTO equipment_in_exercises (exerciseID, equipmentID) VALUES (38, 2);
INSERT INTO exercises (name, difficulty, tips, link) VALUES ('TRX Curl', 0, 'Hold the TRX straps with an underhand grip and lean back and away from the anchor point. Keep your elbows high as you flex your elbows. Pull your fists toward your forehead. ', '');
INSERT INTO muscle_groups_in_exercises (exerciseID, muscleID) VALUES (39, 1);
INSERT INTO equipment (name) VALUES ('TRX');
INSERT INTO equipment_in_exercises (exerciseID, equipmentID) VALUES (39, 8);
INSERT INTO exercises (name, difficulty, tips, link) VALUES ('TRX Hammer Curl', 0, 'Grab the TRX straps and lean away from the anchor point. Maintain a neutral grip. Flex your elbows and pull your fists to either sides of your temples. ', '');
INSERT INTO muscle_groups_in_exercises (exerciseID, muscleID) VALUES (40, 1);
INSERT INTO equipment_in_exercises (exerciseID, equipmentID) VALUES (40, 8);
INSERT INTO exercises (name, difficulty, tips, link) VALUES ('TRX Reverse Curl', 1, 'Stand facing the TRX straps and grasp the handles with both hands, palms facing away from your body. Walk back until the straps are taut, keeping your arms straight. Slowly curl your hands towards your shoulders, keeping your elbows close to your body. ', '');
INSERT INTO muscle_groups_in_exercises (exerciseID, muscleID) VALUES (41, 1);
INSERT INTO equipment_in_exercises (exerciseID, equipmentID) VALUES (41, 8);
INSERT INTO exercises (name, difficulty, tips, link) VALUES ('Barbell Landmine Row', 1, 'Position a barbell in a landmine or corner of a room and stand facing it with your feet shoulder-width apart. Grasp the end of the barbell with a neutral grip, keeping your arms straight and your shoulders relaxed. Bend at the hips and knees to lower your torso until it is almost parallel to the ground. Pull the barbell towards your chest, squeezing your shoulder blades together as you row the weight up. ', '');
INSERT INTO muscle_groups_in_exercises (exerciseID, muscleID) VALUES (42, 1);
INSERT INTO equipment_in_exercises (exerciseID, equipmentID) VALUES (42, 1);
INSERT INTO exercises (name, difficulty, tips, link) VALUES ('Chin Ups', 1, 'Grab the bar shoulder width apart with a supinated grip (palms facing you) With your body hanging and arms fully extended, pull yourself up until your chin is past the bar. Slowly return to starting position. Repeat. ', 'https://youtu.be/y6x0AIhgjsM');
INSERT INTO muscle_groups_in_exercises (exerciseID, muscleID) VALUES (43, 1);
INSERT INTO equipment (name) VALUES ('Bodyweight');
INSERT INTO equipment_in_exercises (exerciseID, equipmentID) VALUES (43, 9);
INSERT INTO exercises (name, difficulty, tips, link) VALUES ('Band Kneeling Single Arm Pulldown', 0, 'Place the band at about eye level. Fall into a kneeling position. The knee that is on the ground should be the same side as the working arm. Stick your chest out. And then pull your elbow straight back. Flex your lats and then extend into the starting position. ', 'https://youtu.be/2bA1aTxHm5U');
INSERT INTO muscle_groups_in_exercises (exerciseID, muscleID) VALUES (44, 1);
INSERT INTO equipment_in_exercises (exerciseID, equipmentID) VALUES (44, 6);
INSERT INTO exercises (name, difficulty, tips, link) VALUES ('Barbell Long Landmine Row', 2, 'Position a barbell in a landmine or corner of a room and stand facing it with your feet shoulder-width apart. Grasp the end of the barbell with an overhand grip, keeping your arms straight and your shoulders relaxed. Bend at the hips and knees to lower your torso until it is almost parallel to the ground. Pull the barbell towards your chest, squeezing your shoulder blades together as you row the weight up. ', '');
INSERT INTO muscle_groups_in_exercises (exerciseID, muscleID) VALUES (45, 1);
INSERT INTO equipment_in_exercises (exerciseID, equipmentID) VALUES (45, 1);
INSERT INTO exercises (name, difficulty, tips, link) VALUES ('Barbell Meadows Row', 1, 'Position a barbell in a landmine or corner of a room and stand facing sideways with a staggered stance and your feet shoulder-width apart. Grasp the end of the barbell with an overhand grip, keeping your arms straight and your shoulders relaxed. Bend at the hips and knees to lower your torso until it is almost parallel to the ground. Pull the barbell towards your chest, squeezing your shoulder blades together as you row the weight up. *Named after the late John Meadows ', '');
INSERT INTO muscle_groups_in_exercises (exerciseID, muscleID) VALUES (46, 1);
INSERT INTO equipment_in_exercises (exerciseID, equipmentID) VALUES (46, 1);
INSERT INTO exercises (name, difficulty, tips, link) VALUES ('Barbell Pronated Pendlay Row', 2, 'Start hinged forward with a flat back and the plates resting on the floor. Make sure your elbows are fully extended. Pull the bar to your sternum. Return the plates back to the floor and let the bar come to a full dead stop. Then initiate the next rep. ', '');
INSERT INTO muscle_groups_in_exercises (exerciseID, muscleID) VALUES (47, 1);
INSERT INTO equipment_in_exercises (exerciseID, equipmentID) VALUES (47, 1);
INSERT INTO exercises (name, difficulty, tips, link) VALUES ('Barbell Pronated Row', 0, 'Grab the barbell with an overhand grip. Hinge forward while keeping your back flat until your torso is about parallel with the ground. Let your arms hang freely. Pull the bar to your torso while tucking your elbows in slightly. Extend your elbows fully before initiating the next rep. ', '');
INSERT INTO muscle_groups_in_exercises (exerciseID, muscleID) VALUES (48, 1);
INSERT INTO equipment_in_exercises (exerciseID, equipmentID) VALUES (48, 1);
INSERT INTO exercises (name, difficulty, tips, link) VALUES ('Barbell Supinated Pendlay Row', 2, 'Begin by standing in front of a barbell with your feet about hip-width apart and your palms facing towards your body. Bend at the hips and knees to grab the barbell with a pronated (overhand) grip, positioning the barbell about an inch away from your shins. Keeping your back straight and your core engaged, use your back muscles to lift the barbell straight up towards your chest. As the barbell reaches your chest, rotate your wrists so that your palms are facing towards your body (supinated grip), and then lower the barbell back to the starting position. ', '');
INSERT INTO muscle_groups_in_exercises (exerciseID, muscleID) VALUES (49, 1);
INSERT INTO equipment_in_exercises (exerciseID, equipmentID) VALUES (49, 1);
INSERT INTO exercises (name, difficulty, tips, link) VALUES ('Barbell Supinated Row', 2, 'Begin by standing in front of a barbell with your feet about hip-width apart. Bend at the hips and knees to grab the barbell with a supinated (underhand) grip, positioning the barbell about an inch away from your shins. Keeping your back straight and your core engaged, use your back muscles to lift the barbell straight up towards your chest. As the barbell reaches your chest, pause briefly, and then lower the barbell back to the starting position. ', '');
INSERT INTO muscle_groups_in_exercises (exerciseID, muscleID) VALUES (50, 1);
INSERT INTO equipment_in_exercises (exerciseID, equipmentID) VALUES (50, 1);
INSERT INTO exercises (name, difficulty, tips, link) VALUES ('Dumbbell Alternating Pendlay Row', 2, 'The dumbbells should be set on the ground. Hinge forward while keeping a flat back. Pull one elbow back toward the ceiling and then return the dumbbell to the ground softly. Then pull with the other arm. ', '');
INSERT INTO muscle_groups_in_exercises (exerciseID, muscleID) VALUES (51, 1);
INSERT INTO equipment_in_exercises (exerciseID, equipmentID) VALUES (51, 2);
INSERT INTO exercises (name, difficulty, tips, link) VALUES ('Dumbbell Kneeling Single Arm Row', 0, 'Place one hand and one knee on a bench. Make sure you keep a flat back. Pull the dumbbell toward the ceiling. Make sure to avoid any jerking or rotating. ', '');
INSERT INTO muscle_groups_in_exercises (exerciseID, muscleID) VALUES (52, 1);
INSERT INTO equipment_in_exercises (exerciseID, equipmentID) VALUES (52, 2);
INSERT INTO exercises (name, difficulty, tips, link) VALUES ('Dumbbell Laying Incline Row', 0, 'Lay on an incline bench that is between 30-45 degrees high. Let your arms hang freely. Pull your elbows straight back toward the ceiling. ', '');
INSERT INTO muscle_groups_in_exercises (exerciseID, muscleID) VALUES (53, 1);
INSERT INTO equipment_in_exercises (exerciseID, equipmentID) VALUES (53, 2);
INSERT INTO exercises (name, difficulty, tips, link) VALUES ('Dumbbell Pendlay Row', 1, 'The dumbbells should be placed on the floor. Hinge forward with a flat back and grab the dumbbells. Pull both dumbbells back and your elbows toward the ceiling. Return the dumbbells back to the floor softly on each rep. ', '');
INSERT INTO muscle_groups_in_exercises (exerciseID, muscleID) VALUES (54, 1);
INSERT INTO equipment_in_exercises (exerciseID, equipmentID) VALUES (54, 2);
INSERT INTO exercises (name, difficulty, tips, link) VALUES ('Dumbbell Single Arm Row', 0, 'Make sure to keep your elbow close to your body and your core engaged throughout the movement. Start by standing in front of a flat bench with your feet shoulder-width apart and holding a dumbbell in one hand. Place your other hand on the bench for support, and bend at the waist so that your chest is parallel to the ground. From this starting position, row the dumbbell up towards your chest, squeezing your shoulder blade together as you do so. ', '');
INSERT INTO muscle_groups_in_exercises (exerciseID, muscleID) VALUES (55, 1);
INSERT INTO equipment_in_exercises (exerciseID, equipmentID) VALUES (55, 2);
INSERT INTO exercises (name, difficulty, tips, link) VALUES ('Dumbbell Single Arm Row Knee', 0, 'Start by placing your elbow on one knee. Hold a dumbbell in your other hand, with your palm facing inwards towards your body. From this starting position, row the dumbbell up towards your chest, keeping your elbow close to your body and your core engaged. ', '');
INSERT INTO muscle_groups_in_exercises (exerciseID, muscleID) VALUES (56, 1);
INSERT INTO equipment_in_exercises (exerciseID, equipmentID) VALUES (56, 2);
INSERT INTO exercises (name, difficulty, tips, link) VALUES ('Plate Pinch Grip Row', 1, 'Make sure to keep your elbow close to your body and your core engaged throughout the movement. Bend forward at your hips while maintaining a flat back. Pull the plate toward your upper abdomen. Lower the weight in a controlled manner and repeat. ', '');
INSERT INTO muscle_groups_in_exercises (exerciseID, muscleID) VALUES (57, 1);
INSERT INTO equipment_in_exercises (exerciseID, equipmentID) VALUES (57, 7);
INSERT INTO exercises (name, difficulty, tips, link) VALUES ('Lawnmower Row', 1, 'Stand with feet staggered and one hand on a bench/box. Hold a dumbbell in the opposing hand. Hinge forward at the hips, keeping the back flat, and eyes facing forward. The weight should hang directly below the shoulders. Start with an overhand grip, as you pull rotate the dumbbell. When your upper arm reaches your side your palm should be facing forward. ', '');
