INSERT INTO exercises (name, difficulty, tips, link) VALUES ('TRX Rotating Row', 0, 'Grab the TRX straps and lean away from the anchor point until the strap is taut. Start with an overhand grip. Pull your elbows back and rotate your grip. By the time your elbows reach your side, your palms should be facing upwards. ', '');
