INSERT INTO exercises (name, difficulty, tips, link) VALUES ('Dips', 1, 'Hold your body with arms locked above the equipment Lower your body slowly while leaning forward, flare out your elbows Raise your body above the bars until your arms are locked. ', 'https://youtu.be/G4BqAiQ9mHs');
