INSERT INTO exercises (name, difficulty, tips, link) VALUES ('Dumbbell Incline Bench Press', 0, 'Raise the bench to a 30 - 45 degree angle Lay on the bench and set your feet on the ground. Raise the dumbbells with straight arms then slowly lower them to about shoulder width. Raise them again until your arms are locked and at the starting position again. ', 'https://youtu.be/bH_T3ibPPzg');
