INSERT INTO exercises (name, difficulty, tips, link) VALUES ('Dumbbell Incline Chest Flys', 0, 'Lay flat on the bench and place your feet on the ground. Begin the exercise with the dumbbells held together above your chest, elbows slightly bent. Simultaneously lower the weights to either side. Pause when the weights are parallel to the bench, then raise your arms to the starting position. ', 'https://www.youtube.com/watch?v=-m4Mozc-z1k');
