INSERT INTO exercises (name, difficulty, tips, link) VALUES ('Dumbbell Bench Press', 0, 'Lay flat on the bench with your feet on the ground. Raise the dumbbells until you have straight arms. Lower the dumbbells to your mid chest Raise the dumbbells until you''ve locked your elbows. ', 'https://youtu.be/VQo4v9zZBlo');
