INSERT INTO exercises (name, difficulty, tips, link) VALUES ('Kettlebell Chest Press', 0, 'Laying on the floor with your knees bent and feet firmly on the floor, hold the kettlebell at your chest with both hands. Fully extend your arms, raising the kettlebell until your elbows are locked. Lower the kettlebell to the starting position and repeat. ', 'https://youtu.be/15CIJwGNVjU');
