INSERT INTO exercises (name, difficulty, tips, link) VALUES ('Kettlebell Single Arm Chest Fly', 0, 'Lie on the floor with your knees bent and feet firmly on the floor, with your arms to the side of your body. Holding a kettlebell in one hand, keeping your arm fully extended with a slight bend in the elbow and your forearms facing upwards, lift the kettlebell above your chest. Lower the kettlebell to the starting position and repeat. ', 'https://youtu.be/bmEIpJjogLg');
