INSERT INTO exercises (name, difficulty, tips, link) VALUES ('Kettlebell Single Arm Press', 0, 'Laying on the floor with your knees bent and feet firmly on the floor, leave one arm resting to the side of the body. Using the other arm, hold the kettlebell at arms length directly upwards of your shoulder. Lower your arm until your upper arm to elbow is resting on the floor. Return to starting position. ', 'https://youtu.be/OaqpmVrrz8E');
