INSERT INTO exercises (name, difficulty, tips, link) VALUES ('Kettlebell Walkover Pushup', 1, 'Starting in a press up position with feet wide and one hand resting on the kettlebell, complete a full press up, lowering your chest towards the ground while keeping your body straight and returning to the starting position. Swap hands on the kettlebell, placing the hand originally on the kettlebell underneath your shoulder, complete another press up. Repeat. ', 'https://youtu.be/foV98ufteFM');
