INSERT INTO exercises (name, difficulty, tips, link) VALUES ('Chest Stretch Variation Four', 0, 'Raise your arms to shoulder height, fully extended in front of you. Slowly bring your arms behind your back still at shoulder height. Pause for a few seconds and then return to starting position. ', 'https://youtu.be/BnhIRqwh_w8');
