INSERT INTO exercises (name, difficulty, tips, link) VALUES ('Chest Stretch Variation Three', 0, 'Place your arms behind your back and clasp your hands together. Slowly extend your elbows until they are locked then lift them away from you. Pause in this position for a few seconds and then return them to starting position. ', 'https://youtu.be/-8o988Co9t0');
