INSERT INTO exercises (name, difficulty, tips, link) VALUES ('Chest Stretch Variation Two', 0, 'Lay on your side with one hand just in front of you. With a slight bend in your arm, rotate your arm around your body as slowly as possible. Return to the starting position and then repeat on the other side. ', 'https://youtu.be/PqLQe9NCYo0');
