INSERT INTO exercises (name, difficulty, tips, link) VALUES ('Incline Push Up', 0, 'Stand facing bench or sturdy elevated platform. Place hands on edge of bench or platform, slightly wider than shoulder width. Slowly lower your body until your chest almost touches the bench. Push body up until arms are extended. ', 'https://youtu.be/NGI2BZGnUuA');
