INSERT INTO exercises (name, difficulty, tips, link) VALUES ('Dumbbell Laying 30 Degree Shrug', 1, 'Lay sideways on a bench letting your arm hang freely. Pull your shoulder blade and joint toward the ceiling. ', '');
