INSERT INTO exercises (name, difficulty, tips, link) VALUES ('Plate Overhead Shrug', 2, 'Stand tall with a weight plate overhead. Elevated and depress your scapula. Avoid leaning forward or backwards to keep pressure off of your lumbar spine. ', '');
