INSERT INTO exercises (name, difficulty, tips, link) VALUES ('Machine Cable V Bar Push Downs', 0, 'Grip the pulley bar with palms facing down at shoulder width. Stand straight with a small forward incline. Keep your upper arms close to your body, and slowly bring the pulley bar down until your arms are fully extended. Pause when you are at the contracted position of the motion, then slowly raise the pulley bar back to the starting point. ', 'https://www.youtube.com/watch?v=tEOhbUBIBwE%7C');
