INSERT INTO exercises (name, difficulty, tips, link) VALUES ('Bench Dips', 0, 'Grip the edge of the bench with your hands, Keep your feet together and legs straight. Lower your body straight down. Slowly press back up to the starting point. TIP: Make this harder by raising your feet off the floor and adding weight. ', 'https://www.youtube.com/watch?v=Z2c02dbshBA%7C');
