INSERT INTO exercises (name, difficulty, tips, link) VALUES ('Kettlebell Skull Crusher', 2, 'Lie on your back with knees in the air and soles of your feet flat on the floor. Hold the kettlebell in both hands directly above your chest. Keeping your upper arms in place, bend your elbows to slowly bring the kettlebell in front of your forehead. Return to the starting position and repeat. ', 'https://youtu.be/B3xhPdah-54');
