INSERT INTO muscle_groups_in_exercises (exerciseID, muscleID) VALUES (250, 5);
INSERT INTO equipment_in_exercises (exerciseID, equipmentID) VALUES (250, 3);
INSERT INTO exercises (name, difficulty, tips, link) VALUES ('Kettlebell Decline Skull Crusher', 2, 'Lie on the floor with your upper back and soles of feet on the floor, knees bent, and pelvis and lower back suspended in the air. Hold the kettlebell in both hands directly above your chest. Keeping your upper arms in place, bend your elbows to bring the kettlebell to the floor above your head. Return the kettlebell to the starting position and repeat. ', 'https://youtu.be/wm5OeiTG2lk');
