INSERT INTO exercises (name, difficulty, tips, link) VALUES ('Kettlebell Tate Press', 1, 'Lie flat on your back. Keeping your arm straight, hold the kettlebell above your chest with your palm facing away from your body. Bend your arm, touching the kettlebell to the top of the shoulder opposite to the arm being worked. Return to the starting position and repeat. ', 'https://youtu.be/ADSscOmWwmY');
