INSERT INTO exercises (name, difficulty, tips, link) VALUES ('Triceps Stretch Variation Three', 0, 'Bend your left arm so your hand is at your shoulder, and hold your elbow with the opposite hand. Gently push on your elbow so it is raised by your head. Hold the stretch for a few seconds then repeat with the other arm. ', 'https://youtu.be/50698xNT9uM');
