INSERT INTO exercises (name, difficulty, tips, link) VALUES ('Triceps Stretch Variation Two', 0, 'Hold a band so it is in line with your spine. Hold the top with your right hand and the bottom with your left. Keeping the top still, pull down on the band. Hold the stretch for a few seconds then repeat with the other arm. ', 'https://youtu.be/GM7rO1GgGr0');
