INSERT INTO exercises (name, difficulty, tips, link) VALUES ('Triceps Stretch Variation One', 0, 'Use the opposite hand to gently apply pressure to the elbow. Hold the stretch for a few seconds then repeat with the other arm. Raise your left arm above your head, and bend at the elbow so your hand is resting at the top of your back. ', 'https://youtu.be/UFCLH__899A');
