INSERT INTO exercises (name, difficulty, tips, link) VALUES ('Diamond Push Ups', 0, 'Position your index fingers and thumbs so they are touching, forming a diamond shape Use a standard push up position Lower your chest towards your hands, keep your elbows close to your body Stop just before your chest touches the floor, then push back up to the starting position. ', 'https://youtu.be/NGI2BZGnUuA');
