INSERT INTO exercises (name, difficulty, tips, link) VALUES ('Barbell Close Grip Bench Press', 1, 'Lay flat on the bench with your feet on the ground. With a narrow grip on the bar, straighten your arms Lower the bar to your lower-mid chest Slowly raise the bar until you''ve locked your elbows. ', 'https://youtu.be/b5tEUN9jsGY');
