INSERT INTO exercises (name, difficulty, tips, link) VALUES ('Cable Push Down', 0, 'You can use any attachment for this. The cable should be set all the way at the top of the machine. Make sure to keep your upper arm glued at your side. Extend your elbows until you feel your triceps contract. ', 'https://youtu.be/qOs9DDWkmxU');
INSERT INTO muscle_groups_in_exercises (exerciseID, muscleID) VALUES (258, 5);
INSERT INTO equipment_in_exercises (exerciseID, equipmentID) VALUES (258, 5);
INSERT INTO exercises (name, difficulty, tips, link) VALUES ('Cable Cross Pushdown', 2, 'Either use two handles or no attachment at all. Both sides of the cable crossover should be set to the top. Stand in the center of the machine holding each Cable in the opposing hand. Your upper arm should be at a 45 degree angle with your torso. Extend your elbows until you feel a contraction and then return to the starting position ', 'https://youtu.be/Dv-Rp8gQfmo');
INSERT INTO muscle_groups_in_exercises (exerciseID, muscleID) VALUES (259, 5);
INSERT INTO equipment_in_exercises (exerciseID, equipmentID) VALUES (259, 5);
INSERT INTO exercises (name, difficulty, tips, link) VALUES ('Cable Skullcrusher', 1, 'You can use a straight bar, rope, easy bar, or two handles. The cable should be set all the way to the top of the machine. Face away from the machine with your elbows up and the attachment just behind your head. Extend at the elbows until you feel a contraction in your triceps and then flex at the elbow back to the starting position. ', 'https://youtu.be/MtTZd-PFe8s');
INSERT INTO muscle_groups_in_exercises (exerciseID, muscleID) VALUES (260, 5);
INSERT INTO equipment_in_exercises (exerciseID, equipmentID) VALUES (260, 5);
INSERT INTO exercises (name, difficulty, tips, link) VALUES ('Cable Overhead Tricep Extension', 1, 'You can use any attachment. The cable should be all the way to the bottom of the machine. Face away from the machine. Point your elbows straight up toward the ceiling. From there, extend your elbows until your fists are pointed straight toward the ceiling. ', 'https://youtu.be/WjepqXc_xhE');
INSERT INTO muscle_groups_in_exercises (exerciseID, muscleID) VALUES (261, 5);
