INSERT INTO exercises (name, difficulty, tips, link) VALUES ('Band Overhead Tricep Extension', 2, 'Stand with one or both feet on band. Go up and over your head with the band to the starting position. Flex and extend your elbows. ', 'https://youtu.be/ke2tFpBYssE');
INSERT INTO muscle_groups_in_exercises (exerciseID, muscleID) VALUES (262, 5);
