INSERT INTO exercises (name, difficulty, tips, link) VALUES ('Band Pushdown', 0, 'Place the band just above eye level. Take a few steps away until the band is taut. Push your hips back slightly. Extend and flex at the elbows. ', 'https://youtu.be/8lGePM05YTk');
