INSERT INTO exercises (name, difficulty, tips, link) VALUES ('Dumbbell Skullcrusher', 1, 'Lay flat on the floor or a bench with your fists extended to the ceiling and a neutral grip. Break at the elbows until your fists are by your temples. Then extend your elbows and flex your triceps at the top. ', 'https://youtu.be/dsfJIf1Dx3A');
