INSERT INTO exercises (name, difficulty, tips, link) VALUES ('Barbell Skullcrusher', 1, 'Take a shoulder width grip. Break at the elbows. Try to keep your elbows tucked in. Don''t let them flare out. Stop the bar a few inches from your forehead and extend your elbows back up. ', 'https://youtu.be/SLu4oek-ECE');
INSERT INTO muscle_groups_in_exercises (exerciseID, muscleID) VALUES (266, 5);
