INSERT INTO equipment_in_exercises (exerciseID, equipmentID) VALUES (268, 2);
INSERT INTO exercises (name, difficulty, tips, link) VALUES ('Dumbbell Rolling Tricep Extension', 2, 'Lay flat on a bench with two dumbbells pointed toward the ceiling. Break at the elbows until your forearms touch your biceps, then flex at the shoulder joint bringing your upper arms to the sides of your head. Bring your elbows back up until they are pointed at the ceiling, then extend your elbows. ', '');
INSERT INTO muscle_groups_in_exercises (exerciseID, muscleID) VALUES (269, 5);
