INSERT INTO exercises (name, difficulty, tips, link) VALUES ('Kettlebell Rear Delt Row', 0, 'Stand with your feet shoulder width apart, holding the kettlebell with both hands in front of your thighs. Bend forward at the hips bringing the kettlebell to the floor while you slightly bend your knees, keeping your back straight. Lift the kettlebell upwards towards your upper chest and then lower the kettlebell- repeat. ', 'https://youtu.be/qOpOzVJUNY8');
