INSERT INTO exercises (name, difficulty, tips, link) VALUES ('Kettlebell Front Raise', 0, 'Stand up straight with feet shoulder width apart, holding the kettlebell in front of your pelvis. Keeping arms straight, raise the kettlebell slightly higher than head level. Return to the starting position and repeat. ', 'https://youtu.be/OgIUqIjIxM8');
