INSERT INTO exercises (name, difficulty, tips, link) VALUES ('Kettlebell Long Lever Lateral Raise', 1, 'Stand with feet shoulder width apart holding a kettlebell with one hand at your side. Keeping your arm straight, swing the kettlebell up and out, away from your body to around head height, holding on to the kettlebell firmly so it does not change position in your hand. Return to the starting position and repeat. ', 'https://youtu.be/8y3iz93y9u0');
