INSERT INTO exercises (name, difficulty, tips, link) VALUES ('Shoulders Stretch Variation Four', 0, 'Stand with you feet shoulder width apart. Place one arm across the front of your chest at shoulder height, press the forearm of your other arm above the elbow, hooking the stretching arm with your hand. Press the arm until it is straight and rotate your upper torso to engage the stretch even deeper. Repeat with your other arm. ', 'https://youtu.be/mpPbbH481a4');
