INSERT INTO exercises (name, difficulty, tips, link) VALUES ('Shoulders Stretch Variation Three', 0, 'Place the top of your hand into the small of your back, your arm at a 90 degree angle. Hold your elbow with the other arm, and slowly pull the arm forward until you feel your shoulder engaged in the stretch. Pause at the apex of the stretch and return to the starting position. Return to starting position, and repeat with your other arm. ', 'https://youtu.be/NQMe2sFiOL4');
