INSERT INTO exercises (name, difficulty, tips, link) VALUES ('Shoulders Stretch Variation Two', 0, 'Start with your arms at either side. Begin by pushing your shoulders forward and in towards your chest. Repeat as necessary. Continue the circular motion with your shoulders, pinching up towards your ears, and then extending your chest outwards while you finish the motion. ', 'https://youtu.be/ZEqdx05Z6K0');
