INSERT INTO exercises (name, difficulty, tips, link) VALUES ('Plate Car Driver', 0, 'Inhale and brace your abdominals Raise the arms vertically while keeping the elbows slightly bent Once the arms are parallel with the floor, rotate the plate clockwise and counterclockwise. Lower the plate back to the starting position. ', '');
