INSERT INTO exercises (name, difficulty, tips, link) VALUES ('TRX W Raise', 0, 'Hold the TRX straps and lean away from the anchor point. Pull your elbows back while externally rotating your shoulder joint. Your arms should make a W shape at the back portion of the exercise. ', '');
