INSERT INTO exercises (name, difficulty, tips, link) VALUES ('Machine Seated Cable Row', 0, 'Sit with your back straight on the machine and grip the handles. Pull the handles back using your arms. Your legs and torso should be at a 90 angle. Push out your chest. Pull the handles towards your body until your hands are beside your abdomen. ', 'https://www.youtube.com/watch?v=-gLQHqEtEpY%7C');
