INSERT INTO exercises (name, difficulty, tips, link) VALUES ('Pull Ups', 1, 'Grasp the bar with an overhand grip, arms and shoulders fully extended. Pull your body up until your chin is above the bar. Lower your body back to starting position. ', 'https://www.youtube.com/watch?v=y6x0AIhgjsM');
INSERT INTO muscle_groups_in_exercises (exerciseID, muscleID) VALUES (345, 7);
