INSERT INTO exercises (name, difficulty, tips, link) VALUES ('Barbell Bent Over Row', 1, 'Grab a barbell with a shoulder width pronated or supinated grip. Bend forward at your hips while maintaining a flat back. Pull the weight toward your upper abdomen. Lower the weight in a controlled manner and repeat. ', 'https://www.youtube.com/watch?v=GqDnbRoXnbY%7C');
