INSERT INTO exercises (name, difficulty, tips, link) VALUES ('Kettlebell Row', 0, 'Stand with your feet shoulder width apart holding the kettlebell with both hands in front of your thighs. Bend forward at the hips bringing the kettlebell to the floor while you slightly bend your knees, keeping your back straight. Lift the kettlebell upwards towards your chest and lower - repeat. ', 'https://youtu.be/DpflFvJnjxo');
INSERT INTO muscle_groups_in_exercises (exerciseID, muscleID) VALUES (348, 7);
INSERT INTO equipment_in_exercises (exerciseID, equipmentID) VALUES (348, 3);
INSERT INTO exercises (name, difficulty, tips, link) VALUES ('Kettlebell Shoulder Extension', 1, 'Stand with your feet shoulder width apart bending forward at the hips as far as comfortable. Place off-hand on the same side thigh for stability. Hold the kettlebell in one hand, straight armed and pointing towards the floor. Keeping your arm straight, swing the kettlebell behind you as far as comfortable. Return to the starting position and repeat. ', 'https://youtu.be/jtFC5E0GCdQ');
INSERT INTO muscle_groups_in_exercises (exerciseID, muscleID) VALUES (349, 7);
INSERT INTO equipment_in_exercises (exerciseID, equipmentID) VALUES (349, 3);
INSERT INTO exercises (name, difficulty, tips, link) VALUES ('Kettlebell Single Arm Row', 0, 'Stand with your feet shoulder width apart, shifting one foot behind you. Hold the kettlebell in the same hand as the leg shifted backwards. Bend forwards at the hips bringing the kettlebell to the floor while you slightly bend your knee, keeping your back straight. Lift the kettlebell upwards towards your chest and lower - repeat. ', 'https://youtu.be/Z7ZWC6UKyD4');
INSERT INTO muscle_groups_in_exercises (exerciseID, muscleID) VALUES (350, 7);
INSERT INTO equipment_in_exercises (exerciseID, equipmentID) VALUES (350, 3);
INSERT INTO exercises (name, difficulty, tips, link) VALUES ('Kettlebell Pullover', 2, 'Rest your upper back on the bench and place soles of your feet firmly on the floor keeping torso and thighs suspended and parallel to the floor. Hold the kettlebell with both hands above your chest, keeping your arms straight. Keeping your arms straight, move the kettlebell in the direction of your head until your arms are parallel to the floor. Return to starting to position and repeat. ', 'https://youtu.be/_q02WKDALQs');
