INSERT INTO exercises (name, difficulty, tips, link) VALUES ('Lats Stretch Variation Three', 0, 'Find a wall to lean against, lay your tricep against the wall with your hand behind your back. Slowly lean into the wall. Return to starting position, then switch sides. ', 'https://youtu.be/goQAAe4HzdA');
