INSERT INTO exercises (name, difficulty, tips, link) VALUES ('Lats Stretch Variation Two', 0, 'Stand up right and raise your hand to a full stretch above your head. Slowly lean across your body and trace the opposite hand down your leg. Hold at the bottom of the stretch, return to starting position and then switch sides. ', 'https://youtu.be/9RaJgODaf7w');
