INSERT INTO exercises (name, difficulty, tips, link) VALUES ('Lats Stretch Variation One', 0, 'Place both hands on the wall in front of you. Slowly lower your body until you feel the stretch. Hold at the bottom of the stretch, return to starting position. ', 'https://youtu.be/RRcKHHEQ3wI');
