INSERT INTO exercises (name, difficulty, tips, link) VALUES ('Inverted Row', 0, 'Lay underneath a fixed horizontal bar and grab the bar with a wide overhand grip. Pull your body straight up to the bar. Return to the starting position, your arms fully extended. Repeat. ', 'https://youtu.be/EuT4-4OFySE');
