INSERT INTO exercises (name, difficulty, tips, link) VALUES ('Machine Hamstring Curl', 0, 'Lay down on the machine, placing your legs beneath the padded lever. Position your legs so that the padded lever is below your calve muscles. Support yourself by grabbing the side handles of the machine, and slowly raise the weight with your legs, toes pointed straight. Pause at the apex of the motion, then slowly return to starting position. ', 'https://www.youtube.com/watch?v=KXYwW0v9gpM%7C');
